import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactPlayer from 'react-player';

function Spot() {
    const CLIENT_ID = "09c8e8afd28146f98aa154ed8bcfb037";
    const REDIRECT_URI = "http://localhost:3000";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token"

    const [token, setToken] = useState('');
    const [searchKey, setSearchKey] = useState('');
    useEffect(() => {
        const hash = window.location.hash;
        let token = window.localStorage.getItem('token')

        if (!token && hash) {
            token = hash.substring(1).split('&').find(element => element.startsWith("access_token")).split('=')[1]

            window.location.hash = "";
            window.localStorage.setItem("token", token);
            
        }

        setToken(token);


    }, [])

        const searchArtists = async (e) => {
            const {data} = await axios.get("https://api.spotify.com/v1/search", {
                headers: {
                    Authorization: `Bearer ${token}` 
                },
                params: {
                    q: searchKey,
                    type: "artist"
                }
            });
        }
    return (
        <div>
            <h1>Spotify React</h1>
            <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>
                Login to Spotify
                </a>

            {token ?
                <form onSubmit={searchArtists}>
                    <input type="text" onChange={e => setSearchKey(e.target.value)} />
                    <button type={"submit"}>Search</button>
                </form>

                : <h2>Please login</h2>
            }
            <ReactPlayer style="border-radius:12px" src="https://open.spotify.com/embed/playlist/7vVFLtugex7cmNGDPPq85E?utm_source=generator" width="50%" height="380" frameBorder="0" allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture" />
        </div>
    )
};

export default Spot;
